    /*post header*/
    .article-meta-tag {
        background: transparent;
        color: #f3f3f3;
        padding: 0;
    }
    .article-metaline,
    .article-meta-value,
    .article-metaline-right {
        background: transparent;
        color: #f3f3f3;
    }
    .article-metaline-right {
        opacity: .5;
    }
    .article-metaline:first-child {
        font-size: 15px;
        padding-top: 75px;
    }
    .article-metaline:nth-child(3) .article-meta-tag {
        display: none;
    }
    .article-metaline:nth-child(3) .article-meta-value {
        left: 0;
        padding: 0;
        position: absolute;
        top: 30px;
        font-size: 24px;
        font-weight: bold;
    }
    .article-metaline:nth-child(4) {
        padding-bottom: 15px;
        border-bottom: 1px solid #414141;
        font-size: 15px;
    }
    [class^="article-metaline"]:nth-child(3n+1) [class^="article-meta"] {
        color: #999;
    }

    /*push messages*/
    .f2 + div.push {
        margin-top: 30px;
    }
    .push + .f2 + div.push {
        margin-top: 0;
    }
    .push {
        box-sizing: border-box;
        background: #343434;
        height: 45px;
        padding-left: 10px;
        border-bottom: 1px solid #414141;
    }
    .push span {
        line-height: 45px;
    }
    .push-content {
        color: #f3f3f3;
    }
    .push-ipdatetime {
        top: 0;
        right: 5px;
        position: relative;
    }
    .push-tag {
        line-height: 30px;
    }

    /*hide repost message*/
    div.push + .f2,
    div.push + .f2 + .f2,
    div.push + .f2 + .f2 + .f2 {
        display: none;
    }
    div.push + .f2 + .f2 + .f2 + div.push {
        position: relative;
        margin-top: -30px;
    }
    .richcontent {
        margin: 0;
    }
    a[href*='png'],
    a[href*='jpg'] {
        display: none;
    }
    a[href*='png']:after,
    a[href*='jpg']:after {
        content: ' :';
    }
    a[href*='png']:hover,
    a[href*='jpg']:hover {
        background-color: transparent;
    }
    @media screen {
        #topbar a:link,
        #topbar a:visited,
        #topbar a:hover {
            text-decoration: underline;
        }
    }
    img {
        box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
        max-height: 500px;
        max-width: 720px;
    }
    img:hover {
        max-height: 1000px;
        max-width: 1000px;
        -moz-transition-property: all;
        -o-transition-property: all;
        -webkit-transition-property: all;
        transition-property: all;
        -moz-transition-duration: 0.5s;
        -o-transition-duration: 0.5s;
        -webkit-transition-duration: 0.5s;
        transition-duration: 0.5s;
        -moz-transition-timing-function: ease-out;
        -o-transition-timing-function: ease-out;
        -webkit-transition-timing-function: ease-out;
        transition-timing-function: ease-out;
    }
    #article-polling {
        box-sizing: border-box;
        max-width: 720px;
