        padding: 10px;
        background: none;
    }
    #navigation-container {
        padding: 10px 0;
        background: #343434;
        box-shadow: 0 0 20px rgba(0, 0, 0, .2);
    }
    #navigation > * {
        padding: 0;
    }
    #navigation .board {
        color: #f3f3f3 !important;
    }
    #navigation .board:hover {
        color: #222 !important;
    }
    #navigation .bar {
        background: none;
        width: auto;
    }
    #navigation .bar:after {
        content: '|';
        margin: 0 10px;
        color: #f3f3f3;
    }
    .bbs-footer-message {
        margin-bottom: 60px;
    }
    #main-content {
        width: 800px;
    }

